import React, { useState, useEffect, useRef } from "react";
import { fabric } from "fabric";
import "./Design.css";

function Design() {
  const canvasRef = useRef(null);
  const previewRef = useRef(null);
  const [canvas, setCanvas] = useState(null);
  const [previewCanvas, setPreviewCanvas] = useState(null);
  const [selectedTool, setSelectedTool] = useState("select");
  const [petType, setPetType] = useState("dog");
  const [petSize, setPetSize] = useState("medium");
  const [baseColor, setBaseColor] = useState("#ffffff");
  const [clothingType, setClothingType] = useState("tshirt");

  // Initialize main canvas
  useEffect(() => {
    if (!canvasRef.current) return;

    try {
      const canvas = new fabric.Canvas(canvasRef.current, {
        width: 800,
        height: 600,
        backgroundColor: "#ffffff",
      });

      // Add base clothing template
      const clothing = new fabric.Rect({
        width: 300,
        height: 400,
        fill: baseColor,
        stroke: "#000000",
        strokeWidth: 2,
        selectable: false,
      });

      canvas.add(clothing);
      canvas.centerObject(clothing);
      setCanvas(canvas);

      // Enable object controls
      canvas.on("object:modified", updatePreview);
      canvas.on("object:added", updatePreview);
      canvas.on("object:removed", updatePreview);

      return () => {
        canvas.dispose();
      };
    } catch (error) {
      console.error("Error initializing canvas:", error);
    }
  }, []);

  // Initialize preview canvas
  useEffect(() => {
    if (!previewRef.current) return;

    try {
      const previewCanvas = new fabric.Canvas(previewRef.current, {
        width: 300,
        height: 300,
        backgroundColor: "#f5f5f5",
      });
      setPreviewCanvas(previewCanvas);

      return () => {
        previewCanvas.dispose();
      };
    } catch (error) {
      console.error("Error initializing preview canvas:", error);
    }
  }, []);

  // Create pet silhouette
  const createPetSilhouette = (type, size) => {
    const scale = size === "small" ? 0.8 : size === "medium" ? 1 : 1.2;

    // Create pet body
    const body = new fabric.Ellipse({
      rx: 100 * scale,
      ry: 60 * scale,
      fill: "#333333",
      left: 150,
      top: 150,
      originX: "center",
      originY: "center",
    });

    // Create head
    const head = new fabric.Circle({
      radius: 40 * scale,
      fill: "#333333",
      left: 150,
      top: 80 * scale,
      originX: "center",
      originY: "center",
    });

    // Create ears
    const ear1 = new fabric.Triangle({
      width: 30 * scale,
      height: 40 * scale,
      fill: "#333333",
      left: 120 * scale,
      top: 60 * scale,
      angle: -30,
    });

    const ear2 = new fabric.Triangle({
      width: 30 * scale,
      height: 40 * scale,
      fill: "#333333",
      left: 180 * scale,
      top: 60 * scale,
      angle: 30,
    });

    // Create tail
    const tail = new fabric.Path("M 150 210 Q 180 180 200 200", {
      stroke: "#333333",
      strokeWidth: 10 * scale,
      fill: "",
    });

    return [body, head, ear1, ear2, tail];
  };

  // Update preview when design changes
  const updatePreview = () => {
    if (!canvas || !previewCanvas) return;

    try {
      // Clear preview canvas
      previewCanvas.clear();

      // Add pet silhouette
      const petParts = createPetSilhouette(petType, petSize);
      petParts.forEach((part) => previewCanvas.add(part));

      // Add clothing base
      const baseClothing = canvas.getObjects()[0];
      if (baseClothing) {
        const clothingClone = fabric.util.object.clone(baseClothing);
        clothingClone.scale(0.5);
        clothingClone.set({
          left: 150,
          top: 150,
          originX: "center",
          originY: "center",
        });
        previewCanvas.add(clothingClone);
        previewCanvas.sendToBack(clothingClone);
      }

      // Add design elements
      canvas.getObjects().forEach((obj, index) => {
        if (index > 0) {
          // Skip base clothing
          const clone = fabric.util.object.clone(obj);
          clone.scale(0.5);
          clone.set({
            left: 150,
            top: 150,
            originX: "center",
            originY: "center",
          });
          previewCanvas.add(clone);
        }
      });

      previewCanvas.renderAll();
    } catch (error) {
      console.error("Error updating preview:", error);
    }
  };

  // Update clothing template when type or color changes
  useEffect(() => {
    if (!canvas) return;

    try {
      const objects = canvas.getObjects();
      if (objects.length > 0) {
        objects[0].set("fill", baseColor);
        canvas.renderAll();
        updatePreview();
      }
    } catch (error) {
      console.error("Error updating template:", error);
    }
  }, [clothingType, baseColor, canvas]);

  // Update preview when pet type or size changes
  useEffect(() => {
    updatePreview();
  }, [petType, petSize]);

  const addText = () => {
    if (!canvas) return;

    try {
      const text = new fabric.IText("Double click to edit", {
        left: 100,
        top: 100,
        fontFamily: "Arial",
        fontSize: 20,
        fill: "#000000",
      });
      canvas.add(text);
      canvas.setActiveObject(text);
    } catch (error) {
      console.error("Error adding text:", error);
    }
  };

  const addImage = (url) => {
    if (!canvas) return;

    try {
      fabric.Image.fromURL(url, (img) => {
        // Calculate scale to fit within canvas
        const maxWidth = canvas.width * 0.8;
        const maxHeight = canvas.height * 0.8;
        const scale = Math.min(maxWidth / img.width, maxHeight / img.height);

        img.scale(scale);
        img.set({
          left: (canvas.width - img.width * scale) / 2,
          top: (canvas.height - img.height * scale) / 2,
        });

        // Add controls for resizing and rotating
        img.setControlsVisibility({
          mt: true, // middle top
          mb: true, // middle bottom
          ml: true, // middle left
          mr: true, // middle right
          bl: true, // bottom left
          br: true, // bottom right
          tl: true, // top left
          tr: true, // top right
          mtr: true, // middle top rotate
        });

        canvas.add(img);
        canvas.setActiveObject(img);
      });
    } catch (error) {
      console.error("Error adding image:", error);
    }
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = (event) => {
      addImage(event.target.result);
    };
    reader.readAsDataURL(file);
  };

  const handleSave = () => {
    if (!canvas) return;

    try {
      const data = canvas.toJSON();
      localStorage.setItem("design", JSON.stringify(data));
      alert("Design saved successfully!");
    } catch (error) {
      console.error("Error saving design:", error);
      alert("Error saving design. Please try again.");
    }
  };

  const handleOrder = () => {
    // Implement order functionality
    alert("Order functionality coming soon!");
  };

  return (
    <div className="design-page">
      <nav className="design-nav">
        <div className="nav-left">
          <h1>Pet Fashion Designer</h1>
        </div>
        <div className="nav-right">
          <button onClick={handleSave} className="nav-button">
            Save Design
          </button>
          <button onClick={handleOrder} className="nav-button primary">
            Order Now
          </button>
        </div>
      </nav>

      <div className="design-container">
        {/* Left Toolbar */}
        <div className="toolbar left-toolbar">
          <div className="tool-section">
            <h3>Clothing Type</h3>
            <select
              value={clothingType}
              onChange={(e) => setClothingType(e.target.value)}
              className="tool-select"
            >
              <option value="tshirt">T-Shirt</option>
              <option value="hoodie">Hoodie</option>
              <option value="sweater">Sweater</option>
            </select>
          </div>

          <div className="tool-section">
            <h3>Base Color</h3>
            <input
              type="color"
              value={baseColor}
              onChange={(e) => setBaseColor(e.target.value)}
              className="color-picker"
            />
          </div>

          <div className="tool-section">
            <h3>Add Elements</h3>
            <button onClick={addText} className="tool-button">
              Add Text
            </button>
            <label className="tool-button">
              Add Image
              <input
                type="file"
                accept="image/*"
                onChange={handleImageUpload}
                style={{ display: "none" }}
              />
            </label>
          </div>

          <div className="tool-section">
            <h3>Tools</h3>
            <button
              onClick={() => setSelectedTool("select")}
              className={`tool-button ${
                selectedTool === "select" ? "active" : ""
              }`}
            >
              Select
            </button>
            <button
              onClick={() => setSelectedTool("move")}
              className={`tool-button ${
                selectedTool === "move" ? "active" : ""
              }`}
            >
              Move
            </button>
          </div>
        </div>

        {/* Canvas */}
        <div className="canvas-container">
          <canvas ref={canvasRef} />
        </div>

        {/* Right Preview Panel */}
        <div className="toolbar right-toolbar">
          <div className="tool-section">
            <h3>Pet Preview</h3>
            <select
              value={petType}
              onChange={(e) => setPetType(e.target.value)}
              className="tool-select"
            >
              <option value="dog">Dog</option>
              <option value="cat">Cat</option>
            </select>

            <select
              value={petSize}
              onChange={(e) => setPetSize(e.target.value)}
              className="tool-select"
            >
              <option value="small">Small</option>
              <option value="medium">Medium</option>
              <option value="large">Large</option>
            </select>

            <div className="preview-container">
              <canvas ref={previewRef} />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Design;
